public class Task21 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	//create an array of int and find largest num
		

	int[] numbers= {200,20,13,499,65};
	int largest=numbers[0];//we will assume that the largest is 200 element
	
	for(int i=0; i<numbers.length;i++) {
		
		if (numbers[i]>largest) {
			largest=numbers[i];
			System.out.println("this is the largest num "+largest);
		
		 
	
	}
	
	
	
	}

}}
