package com.class11;

public class TaskArraysLoops31 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//---------for loop
		
		String[] cars= {"bmw","audi","porsche","mazeratti","lexus","mercedes"};
	
	 for (int i=0;i<cars.length;i++) {
		 System.out.println(cars[i]);
	 }
	
//--------advanced for loop	
	
	 String[] cars2= {"bmw","audi","porsche","mazeratti","lexus","mercedes"};
		
	 for (String lollipop:cars2) {
		 System.out.print(lollipop+" ");
	 }
	
	
	 
	 
	 
	 
	 
	 
	
	}

}
