package com.class2;

public class perimeterAndArea {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//the perimeter of a rectangle with width --and height ---is equal to----

		int width=5;
		int height=8;
		
		int perimeter=2*(width+height);
		
		int area=width*height;
		System.out.println("the perimeter of a rectangle with width "+width+" and height "+height+" is equal to "+perimeter+" and the area is "+area);
	
	
		
		
		//
		int width1=6;
		int height2=9;
		int p=2*(width1+height2);
		int area1=width1*height2;
		
		System.out.println("the perimeter of a rectangle with width "+width1+" and height "+height2+" is equal to "+p+"and the area is " +area1);
		
	
	}

}
