package com.class2;

public class twoDecimalValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//h.w
		//double example for 14-15 decimals;
	    //1."the ----of 2 numbers---and ---is equal to----"
	    double e=10.4;
	    double f=5.2;
	    
	    double sum=e+f;
	    
		double sub=e-f;
		
		double mult=e*f;
		double div=e/f;
		System.out.println("the sum of 2 numbers "+e+"and "+f+" is equal to"+sum);
		System.out.println("the sub of 2 numbers "+e+"and "+f+" is equal to"+sub);
		System.out.println("the mult of 2 numbers "+e+"and "+f+" is equal to"+mult);
		System.out.println("the div of 2 numbers "+e+"and "+f+" is equal to"+div);
		
		
		//float example for 2 decimals;
		
		float a=5.2f;
		float b=3.6f;
		
		float sum1=a+b;
		float sub2=a-b;
		float mult3=a*b;
	    float div4=a/b;
		
	    System.out.println("the sum of 2 numbers "+a+" and "+b+" is equal to "+sum1);
		System.out.println("the sum of 2 numbers "+a+" and "+b+" is equal to "+sub2);
		System.out.println("the sum of 2 numbers "+a+" and "+b+" is equal to "+ mult3);
		System.out.println("the sum of 2 numbers "+a+" and "+b+" is equal to "+div4);
		
		
		
	}

}
