package com.class7;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    
		
		///// Do While case //////////// 
		
	int a=10;
	
	do {      ///executing first wo looking if its true or false,let it do whatever it needs to do
		System.out.println("hello");
	a++;
	
	}while (a<15);      //only in while we check condition
		
	
	////////with While case /////////
	
	int b=10;
	
	while(b<15) {////checks condition first,going to be executed if its true
		System.out.println("bye");
	b++;
	}
	
	
	
	}

}
