package com.class8;

public class initializationForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	for (int i=0; i<=5; i++) {
		System.out.println("good morning "+i);
	}
	
	
	////////////////////////////////////////////
	//i want to print numbers from 10-1
	for(int i=10; i>=1; i--) {
		System.out.println(i);//if we know how many times we have to loop or execute
	}
	
	////////////////////////
	//whats the output?
	 
	//////1////////2////4////
	for(int a=0; a<=20; a+=2) {
		////3/////
		System.out.println(a);
	 }
	
	
	for (int b=1; b<=501; b+=5) {
		System.out.println(b);
	}
	

	}
	
	
	}


